import React, {useEffect} from 'react';

import { connect } from 'react-redux';

import { selectCollection } from '../../redux/shop/shop.selectors';

import CollectionItem from '../../components/collection-item/collection-item.component';

// import './collection.styles.scss'
import {CollectionPageContainer, CollectionTitle, CollectionItemsContainer} from './collection.styles';
import firestore from '../../firebase/firebase.utils'
const CollectionPage = ({ collection }) => {

    useEffect(()=> {
        console.log('im subscribing');
        const unsubscribeFromColections = firestore
            .collection('collections')
            .onSnapshot(snapshot => console.log(snapshot));

            return ()=> {
                console.log('im uunsubscribe')
                unsubscribeFromColections()
            }

    })

    const {items, title} = collection;
    return (
        <CollectionPageContainer>
            <CollectionTitle>{title} </CollectionTitle>
            <CollectionItemsContainer>
                {
                    items.map(item => <CollectionItem key={item.id} item={item}/> )
                }
            </CollectionItemsContainer>
        </CollectionPageContainer>
    );
};


const mapStateToPtops = (state, ownProps) => ({
    collection: selectCollection(ownProps.match.params.collectionId)(state)
})


export default connect(mapStateToPtops)(CollectionPage);
