import React from 'react';
import { Route } from 'react-router-dom';
import {connect} from 'react-redux'

import updateCollections from '../../redux/'

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';

import {firestore, convertCollectionsSnapshotToMap} from '../../firebase/firebase.utils';

class ShopPage extends React.Component {

  unsubscribeFromSnapshot = null;

  componentDidMount() {
    const collectionRef = firestore.collection('collections');

    collectionRef.onSnapshot( async snapshot => {
      const collectionMaps = convertCollectionsSnapshotToMap(snapshot);
      console.log(collectionMaps)
    })
  }
    
  render() {
    const {match} = this.props
    return (
      <div className='shop-page'>
      <Route exact path={`${match.path}`} component={CollectionsOverview} />
      <Route path={`${match.path}/:collectionId`} component={CollectionPage} />
    </div>
    )
  }
}

const mapDispatchToProps = dispatch => ({
  updateCollections: dispatch(updateCollections(collectionMaps))
})

export default connect()(ShopPage);
